  | "bounce" // Sıçrayıcı sıfırlama animasyonu
  | "elastic" // Pürüzsüz elastik sıfırlama
  | "magnetic" // Çekim benzeri hareket
  | "float" // Gölgeler ile yüzen efekt
  | "parallax"; // Çok katmanlı hareket efekti

// Eksen kontrolü
type TiltAxis =
  | "both" // Tam X ve Y ekseni hareketi
  | "x" // Yalnızca yatay hareket
  | "y"; // Yalnızca dikey hareket

// Zamanlama konfigürasyonu
type TiltTiming = {
  duration?: number; // Milisaniye cinsinden fare yanıt süresi (varsayılan: 200)
  reset?: number; // Milisaniye cinsinden sıfırlama animasyon süresi (varsayılan: 400)
  easing?: string; // CSS yumuşatma işlevi (varsayılan: "ease-out")
};

// Parıltı efekt ayarları
type GlareSettings = {
  enabled?: boolean; // Parıltı efektini etkinleştir (varsayılan: false)
  maxOpacity?: number; // Maksimum parıltı opaklığı (varsayılan: 0.3)
  color?: string; // Parıltı rengi (varsayılan: "rgba(255,255,255,0.3)")
  position?: "center" | "mouse"; // Parıltı konumlandırması (varsayılan: "center")
};
